mapper.select:
SELECT * FROM BO_EMPLOYEE WHERE 1=1
AND EMPLOYEENAME like #{employeeName}
ORDER BY EMPLOYEENUMBER ASC

SELECT * FROM BO_EMPLOYEE WHERE 1=1 AND EMPLOYEENUMBER LIKE 'S%'
AND EMPLOYEENAME like #{employeeName}
ORDER BY EMPLOYEENUMBER ASC

SELECT * FROM BO_ASSISTANT WHERE 1=1
AND ASSISTANTNAME like #{assistantName}
ORDER BY OPTTIME DESC

SELECT * FROM BO_EMPLOYEE WHERE 1=1
AND EMPLOYEENAME like #{employeeName}
AND picfile IS NULL ORDER BY OPTTIME DESC
